// High-level analysis orchestration using Riverpod and Clean Arch
final analyzeMessageProvider = StateNotifierProvider<MessageAnalysisController, AsyncValue<AnalysisResult>>((ref) {
  return MessageAnalysisController(ref.watch(analyzeMessageUseCaseProvider));
});

class MessageAnalysisController extends StateNotifier<AsyncValue<AnalysisResult>> {
  final AnalyzeMessageUseCase _useCase;
  MessageAnalysisController(this._useCase) : super(const AsyncValue.data(AnalysisResult.idle()));

  Future<void> process(String text) async {
    state = const AsyncValue.loading();
    final result = await _useCase(MessageText(text)); // Domain logic isolation
    state = result.fold(
      (failure) => AsyncValue.error(failure.message, StackTrace.current),
      (analysis) => AsyncValue.data(analysis),
    );
  }
}

