// Clean Arch + TDD Implementation (Movie Feature)
@JsonSerializable()
class MovieModel extends Movie {
  MovieModel({
    required int id,
    required String title,
    required String overview,
  }) : super(id: id, title: title, overview: overview);

  factory MovieModel.fromJson(Map<String, dynamic> json) => _$MovieModelFromJson(json);
}

// Repository Abstraction for TDD Mocking
abstract class MovieRepository {
  Future<Either<Failure, List<Movie>>> getPopularMovies();
}

// Usecase Implementation (Domain Core)
class GetPopularMovies implements UseCase<List<Movie>, NoParams> {
  final MovieRepository repository;
  GetPopularMovies(this.repository);

  @override
  Future<Either<Failure, List<Movie>>> call(NoParams params) async {
    return await repository.getPopularMovies();
  }
}

