// Lightweight State Pattern Implementation
abstract class ProductState {}
class ProductLoading extends ProductState {}
class ProductSuccess extends ProductState { final List<Product> products; ProductSuccess(this.products); }
class ProductError extends ProductState { final String message; ProductError(this.message); }

class ProductController extends ValueNotifier<ProductState> {
  final ProductRepository _repo;
  ProductController(this._repo) : super(ProductLoading());

  Future<void> fetch() async {
    value = ProductLoading();
    try {
      final list = await _repo.getProducts();
      value = ProductSuccess(list);
    } catch (e) {
      value = ProductError(e.toString());
    }
  }
}

