// SOLID + Firebase Realtime Implementation
abstract class IRecipeDatasource {
  Future<void> create(RecipeModel recipe);
  Stream<List<RecipeModel>> list();
}

class FirebaseRecipeDatasource implements IRecipeDatasource {
  final FirebaseDatabase _db;
  FirebaseRecipeDatasource(this._db);

  @override
  Future<void> create(RecipeModel recipe) async {
    await _db.ref('recipes').push().set(recipe.toJson());
  }

  @override
  Stream<List<RecipeModel>> list() {
    return _db.ref('recipes').onValue.map((event) {
      // Reactive mapping logic
      return (event.snapshot.value as Map).entries.map((e) => RecipeModel.fromMap(e.value)).toList();
    });
  }
}

